package module1;

public class Task_8_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 10;
        result += 5; // result = result + 5;
        System.out.println("After addition : " + result); // 15
        result *= 2; // result = result * 2;
        System.out.println("After multiplication : " + result); // 30
        result -= 10; // result = result - 10;
        System.out.println("After subtraction : " + result); // 20
        result /= 4; // result = result / 4;
        System.out.println("After division : " + result); // 5
        result %= 3; // result = result % 3;
        System.out.println("After modulo : " + result); // 2

	}

}
